mapper.select:
select admin_id, admin_pw, admin_name, admin_date_late
  from admin_tbl
 where admin_id = #{admin_id} and admin_pw = #{admin_pw}

mapper.update:
update admin_tbl
   set admin_date_late = sysdate
 where admin_id = #{admin_id}

mapper.select:

select count(*)
from admin_tbl			
where admin_id = #{admin_id}

mapper.update:
update admin_tbl		
    set admin_date_late = sysdate
where admin_id = #{admin_id}
